function is_command { command -v "$@" >/dev/null 2>&1; };sudo echo "" || exit;is_command brew || /usr/bin/ruby -e "$(curl -fsSL https://raw.githubusercontent.com/Homebrew/install/master/install)";mkdir -p ~/atk/temp;is_command git || brew install git;is_command rbenv || brew install rbenv;rbenv install --list | grep '^2.5.5' || brew upgrade rbenv;rbenv versions | grep '2.5.5' || rbenv install 2.5.5;rbenv global 2.5.5;touch ~/.bash_profile;touch ~/.zshenv;cat ~/.bash_profile | grep "eval \"\$(rbenv init -)\"" || echo "is_command rbenv && eval \"\$(rbenv init -)\" # setup for ruby" >> ~/.bash_profile;cat ~/.bash_profile | grep "atk___completions" || echo 'atk___completions() { COMPREPLY=($(compgen -W "$(ruby -e '"'"'require File.dirname(Gem.find_latest_files("atk_toolbox")[0])+"/atk/autocomplete.rb"; Atk.autocomplete("_")'"'"')" "${COMP_WORDS[1]}")); };complete -F atk___completions _' >> ~/.bash_profile;cat ~/.zshenv | grep "eval \"\$(rbenv init -)\"" || echo "is_command rbenv && eval \"\$(rbenv init -)\" # setup for ruby" >> ~/.zshenv;rbenv init &>/dev/null;alias ruby="$(rbenv which ruby)";alias gem="$(rbenv which gem)";ln -s \"/Users/$(whoami)/.rbenv/shims/ruby\" /usr/local/bin/ruby;"$(rbenv which gem)" install atk_toolbox;curl -fsSL https://raw.githubusercontent.com/aggie-tool-kit/atk-stager/master/setup.rb > ~/atk/temp/setup.rb;"$(rbenv which ruby)" ~/atk/temp/setup.rb;rm ~/atk/temp/setup.rb